  {
    "title": "To Have and To Hold",
    "id": "https://www.feedbooks.com/book/6690",
    "author": {
      "name": "Mary Johnston",
      "uri":
          "https://catalog.feedbooks.com/publicdomain/browse/recent.atom?author_id=2162&lang=en"
